use std::collections::HashMap;

impl Solution {
    pub fn three_sum(mut nums: Vec<i32>) -> Vec<Vec<i32>> {
        let mut ret: Vec<Vec<i32>> = Vec::new();
        let mut map: HashMap<i32, i32> = HashMap::new(); // 统计每个数字出现的次数
        for i in nums.iter() {
            map.entry(*i).and_modify(|x| *x+=1).or_insert(1);
        }
        nums = map.keys().cloned().collect(); // 去重以后的nums，每个数字只出现一次
        nums.sort();
        for i in 0..nums.len() {
            if nums[i] == 0 {
                if map.get(&0).unwrap() > &2 {
                    ret.push(vec![0, 0, 0]);
                }
                break;
            }
            
            if nums[i] > 0 {
                break;
            }
            
            let next = if map.get(&nums[i]).unwrap() > &1 { i } else { i + 1};
            for j in next..nums.len() {
                let c = 0 - nums[i] - nums[j];
                if c < nums[j] || (c == nums[j] && map.get(&nums[j]).unwrap() == &1) {
                    break;
                }
                
                if map.contains_key(&c) {
                    ret.push(vec![nums[i], nums[j], c]);
                }
            }
        }
        ret
    }
}

// end submission-----------------------

