use std::collections::{HashSet, HashMap};
use std::cmp::min;

impl Solution {
    pub fn three_sum(nums: Vec<i32>) -> Vec<Vec<i32>> {
        // 将vec中每个重复的数字最多保留两个，如果是0最多只需要保存三个
        // 这段逻辑实现有点复杂，不知道怎么简化
        let mut map: HashMap<i32, i32> = HashMap::new();
        for i in nums.into_iter() {
            map.entry(i).and_modify(|v| *v += 1).or_insert(1);
        }
        let mut nums = Vec::<i32>::new();
        for (k, v) in map.iter() {
            if *k == 0 {
                for _ in 0..min(3, *v) {
                    nums.push(*k);
                }
            } else {
                for _ in 0..min(2, *v) {
                    nums.push(*k);
                }
            }
        }
        nums.sort(); // 排序
        
        // 两个数相加，然后用二分查找找第三个数，复杂度是N*N*LOG(N)，比N^3稍微简单。
        let mut ret: Vec<Vec<i32>> = Vec::new();
        let mut set: HashSet<(i32, i32, i32)> = HashSet::new();
        for i in 0..nums.len() {
            for j in i+1..nums.len() {
                let left = 0 - nums[i] - nums[j];
                match &nums[j+1..].binary_search(&left) {
                    Ok(k) => {
                        if !set.contains(&(nums[i], nums[j], nums[*k+j+1])) {
                            ret.push(vec![nums[i], nums[j], nums[*k+j+1]]);
                            set.insert((nums[i], nums[j], nums[*k+j+1]));
                        }
                    },
                    _ => {},
                }
            }
        }

        ret
    }
}

// end submission-----------------------

struct Solution;

