// 按照2sum改的，居然更慢了
use std::collections::HashSet;
impl Solution {
    pub fn three_sum(mut nums: Vec<i32>) -> Vec<Vec<i32>> {
        let mut ret: Vec<Vec<i32>> = Vec::new();
        nums.sort();
        // 用2sum的思路来解，2sum的复杂度为O(n),这个应该就是O(N*N)
        let mut i: usize = 0;
        while i + 2 < nums.len() {
            if nums[i] > 0 { // 已经排序过了
                break;
            }
            let mut j = i + 1;
            let min = nums[i] + nums[j];
            let mut set: HashSet<i32> = HashSet::new();
            while j < nums.len() {
                match set.get(&(-nums[i]-nums[j])) {
                    Some(&value) => {
                        ret.push(vec![nums[i], value, nums[j]]);
                        // 去重，第三个元素相等的跳过即可
                        let vj = nums[j];
                        while j < nums.len() && nums[j] == vj {
                            j += 1;
                        }
                    }
                    _ => {
                        if nums[j] > -min {
                            break;
                        }
                        set.insert(nums[j]);
                        j += 1;
                    }
                }
            }
            
            // 同样是去重，第一个元素相等的直接跳过
            let vi = nums[i];
            while i < nums.len() && nums[i] == vi {
                i += 1;
            }
        }
        ret
    }
}

// end submission-----------------------

